import React, { useState } from "react";
import "./App.scss";
import Sucess from "./components/sucess/sucess";
import { ErrorHandler } from "./components/error/error";
// import {
//   InitialsValidation,
//   EmailValidation,
//   PasswordValidation,
//   PasswordConfirmation
// } from "./components/validation/validation";
import Input from "./components/input/input";

const validInputs = {
  validInitials: RegExp(/^[a-zA-Z]{3,100}/),
  validEmail: RegExp(
    /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i
  ),
  validPassword: RegExp(
    /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})/
  )
};

const App = () => {
  const [user, setUser] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    signedUp: false,
    disabled: true
  });

  const [error_user, setError_user] = useState(false);
  const [error_email, setError_email] = useState(false);
  const [error_password, setError_password] = useState(false);
  const initialsValidation = value => {
    const { validInitials } = validInputs;
    if (value) {
      return validInitials.test(value);
    }
  };
  const EmailValidation = value => {
    const { validEmail } = validInputs;
    if (value) {
      return validEmail.test(value);
    }
  };

  const handleInputChange = e => {
    e.preventDefault();
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
    if (name === "firstName" || name === "lastName") {
      initialsValidation(value) ? setError_user(false) : setError_user(true);
    }
    if (name === "email") {
      EmailValidation(value) ? setError_email(false) : setError_email(true);
    }
  };

  const {
    firstName,
    lastName,
    email,
    password,
    confirmPassword,
    signedUp,
    disabled
  } = user;

  const userError = error_user ? <ErrorHandler name="firstName" /> : null;

  if (!signedUp) {
    // onSubmit={this.onSubmit}
    return (
      <form className="form">
        <label> Sign Up</label>
        <Input
          name="firstName"
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={handleInputChange}
          error={errorHandler}
        />
        {userError}

        {/* <input
          name="firstName"
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={handleInputChange}
        />

        <input
          name="lastName"
          type="text"
          placeholder="Last Name"
          value={lastName}
          onChange={handleInputChange}
        />

        <InitialsValidation name="lastName" value={lastName} />

        <input
          name="email"
          type="email"
          placeholder="Email"
          value={email}
          onChange={handleInputChange}
        />
        <EmailValidation name="email" value={email} />
        <input
          name="password"
          type="password"
          placeholder="Password"
          value={password}
          onChange={handleInputChange}
        />
        <PasswordValidation
          name="password"
          value={password}
          disabled={disabled}
        />
        <input
          name="confirmPassword"
          type="password"
          placeholder="Verify Password"
          value={confirmPassword}
          onChange={handleInputChange}
        />
        <PasswordConfirmation
          password={password}
          confirmation={confirmPassword}
          name="confirmation"
        /> */}
        <button disabled={disabled} type="submit">
          Submit
        </button>
      </form>
    );
  }
  return <Sucess name={firstName} lastName={lastName} />;
};
export default App;

